import React, { Component } from 'react';

class Post extends Component {

    state = {
        id: null
    }
    componentDidMount(){
        let id = this.props.match.params.post_id;
        console.log()
        axios.get('https://jsonplaceholder.typicode.com/posts/' + id)
          .then(res => {
            this.setState({
              post: res.data
            });
            //console.log(res.data);
          });
      }
    render() {
    

        return (
            <div>
                <h2>{this.state.id}</h2>
            </div>
        )
    }
}

export default Post;
